d("blog-feed");blogPosts.forEach((post=>{const el=document.createElement("div");el.className="log-entry";el.innerHTML=`\n          <div class="log-title">${post.title}</div>\n          <div class="log-meta">[${post.date}] :: <span class="glitch-tag">${post.tags.join(", ")}</span></div>\n          <div class="log-body">${post.content}</div>\n        `;blogFeed.appendChild(el)}));