gsap.registerPlugin(TextPlugin);function scrambleText(element,text,chars){const characters=chars.split("");let scrambledText="";for(let i=0;i<text.length;i++){const randomChar=characters[Math.floor(Math.random()*characters.length)];scrambledText+=randomChar}element.textContent=scrambledText}function startScrambling(element,text,chars){const scrambleDuration=1;const interval=setInterval((()=>{scrambleText(element,text,chars)}),100);setTimeout((()=>{clearInterval(interval);gsap.to(element,{duration:1,text:{value:text,delimiter:""},ease:"power3.out"})}),scrambleDuration*1e3)}document.addEventListener("DOMContentLoaded",(()=>{const scrambleOnLoad=document.querySelector("span#scrambleOnLoad");const scrambleTextOnLoad=scrambleOnLoad.textContent;const scrambleLinks=document.querySelectorAll("span.scrambleText");const chars="ABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890*#@/*!%&^";startScrambling(scrambleOnLoad,scrambleTextOnLoad,chars);scrambleLinks.forEach((element=>{const originalText=element.textContent;element.addEventListener("mouseenter",(()=>{startScrambling(element,originalText,chars)}))}));document.querySelectorAll("span.scrambleText").forEach((element=>{const originalText=element.textContent;element.parentElement.addEventListener("mouseenter",(()=>{startScrambling(element,originalText,chars)}))}))}));